import got from "got";
import { flatfileRootUrl } from "../constants.js";
const endpoints = {
    records: (sheetId) => `${flatfileRootUrl}/sheets/${sheetId}/records`,
    workbooks: `${flatfileRootUrl}/workbooks`
};
export const createWorkbook = async (workbook, token) => {
    return await got.post(endpoints.workbooks, {
        headers: {
            'Content-Type': 'application/json',
            Accept: 'application/json',
            Authorization: `Bearer ${token}`
        },
        json: workbook
    }).json();
};
export const insertRecordsIntoSheet = async (sheetId, records, token) => {
    return await got.post(endpoints.records(sheetId), {
        headers: {
            'Content-Type': 'application/json',
            Accept: 'application/json',
            Authorization: `Bearer ${token}`,
        },
        json: records
    }).json();
};
